import { Component, OnInit } from '@angular/core';
import { FormGroup,FormControl, Validators } from '@angular/forms';

@Component({
  selector: 'app-page-post',
  templateUrl: './page-post.component.html',
  styleUrls: ['./page-post.component.css']
})
export class PagePostComponent implements OnInit {
constructor(){}

genders = ['male', 'female'];
signupForm: FormGroup;
 ngOnInit(): void {
   this.signupForm = new FormGroup ({
     userData: new FormGroup ({
       'username': new FormControl('Pavan Nagadiya', Validators.required),
     'email': new FormControl('nagadiyap@gmail.com', [Validators.required, Validators.email]),
     }),
     'gender': new FormControl('male'),
   })
 }
 onSubmit() {
   console.log(this.signupForm.value);
 }
}
