import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, ObservableInput, catchError, retry } from 'rxjs';
@Injectable({
  providedIn: 'root'
})
export class PageService {
apiUrl='https://jsonplaceholder.typicode.com/comments';
  handleError: ((err: any, caught: Observable<unknown>) => ObservableInput<any>) | undefined;
  constructor(private http:HttpClient) { }


  getComment():Observable<any>{

    return this.http.get<any>(this.apiUrl).pipe(retry(1));
  }

}
